ER' pour confirmer :\e[0m"        echo -ne "\e[1;36mâ†’ \e[0m"        read -r CONFIRMATION        if [[ "$CONFIRMATION" == "RAZ $TARGET_USER" ]]; then            rm -rf "$docker_wg_path"/* "$docker_wg_path"/.??* 2>/dev/null            echo -e "\n\e[1;32mâœ“ Dossier docker-wireguard rÃ©initialisÃ© pour $TARGET_USER\e[0m"        else            echo -e "\n\e[1;33mOpÃ©ration annulÃ©e\e[0m"        fi        echo -e "\n\e[1;32mAppuyez sur une touche pour continuer...\e[0m"        read -n1 -s    else        echo -e "\n\e[1;31mSÃ©lection invalide\e[0m"        echo -e "\n\e[1;32mAppuyez sur une touche pour continuer...\e[0m"        read -n1 -s    fi}# Fonctions de gestion Dockercheck_and_install_docker() {    if ! command -v docker &> /dev/null; then        echo -e "\n\e[1;31mDocker n'est pas installÃ©. Installation en cours...\e[0m"        install_docker    else        echo -e "\n\e[1;32mDocker est dÃ©jÃ  installÃ©.\e[0m"    fi}install_docker() {    # Mise Ã  jour des paquets    echo -e "\n\e[1;34mMise Ã  jour des paquets...\e[0m"    apt-get update -y    # Installation des paquets nÃ©cessaires    echo -e "\n\e[1;34mInstallation des paquets nÃ©cessaires...\e[0m"    apt-get install -y apt-transport-https ca-certificates curl software-properties-common    # Ajout de la clÃ© GPG de Docker    echo -e "\n\e[1;34mAjout de la clÃ© GPG de Docker...\e[0m"    curl -fsSL https://download.docker.com/linux/ubuntu/gpg | sudo apt-key add -    # Ajout du dÃ©pÃ´t Docker    echo -e "\n\e[1;34mAjout du dÃ©pÃ´t Docker...\e[0m"    add-apt-repository "deb [arch=amd64] https://download.docker.com/linux/ubuntu $(lsb_release -cs) stable"    # Installation de Docker    echo -e "\n\e[1;34mInstallation de Docker...\e[0m"    apt-get update -y    apt-get install -y docker-ce    # VÃ©rification de l'installation    if command -v docker &> /dev/null; then        echo -e "\n\e[1;32mDocker installÃ© avec succÃ¨s.\e[0m"    else        echo -e "\n\e[1;31mÃ‰chec de l'installation de Docker.\e[0m"    fi}
